#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\Project\admin\soybean-admin\node_modules\.pnpm\rimraf@5.0.5\node_modules\rimraf\dist\esm\node_modules;D:\Project\admin\soybean-admin\node_modules\.pnpm\rimraf@5.0.5\node_modules\rimraf\dist\node_modules;D:\Project\admin\soybean-admin\node_modules\.pnpm\rimraf@5.0.5\node_modules\rimraf\node_modules;D:\Project\admin\soybean-admin\node_modules\.pnpm\rimraf@5.0.5\node_modules;D:\Project\admin\soybean-admin\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/Project/admin/soybean-admin/node_modules/.pnpm/rimraf@5.0.5/node_modules/rimraf/dist/esm/node_modules:/mnt/d/Project/admin/soybean-admin/node_modules/.pnpm/rimraf@5.0.5/node_modules/rimraf/dist/node_modules:/mnt/d/Project/admin/soybean-admin/node_modules/.pnpm/rimraf@5.0.5/node_modules/rimraf/node_modules:/mnt/d/Project/admin/soybean-admin/node_modules/.pnpm/rimraf@5.0.5/node_modules:/mnt/d/Project/admin/soybean-admin/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../rimraf/dist/esm/bin.mjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../rimraf/dist/esm/bin.mjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../rimraf/dist/esm/bin.mjs" $args
  } else {
    & "node$exe"  "$basedir/../rimraf/dist/esm/bin.mjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
